import { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { Container, Button, Form } from "react-bootstrap";
import { FaPaperPlane, FaLink } from "react-icons/fa";
import axios from "axios";
import { ChatBubble } from "./ChatBubble";
import Cookies from "js-cookie";
import Register from "./Register";
import HomePage from "./HomePage";

function SocketCLientOne() {
	useEffect(() => {
		const myCookieValue = Cookies.get("anshul-chat-user");

		if (myCookieValue) {
			setSenderEmail(myCookieValue);
		}
	}, []);

	const socket = io("http://localhost:5001/", {
		withCredentials: true,
		extraHeaders: {
			ConnectWithAnshul: "Sure",
		},
	});

	const [successfullyConnected, setConnection] = useState(false);
	const [chatMessage, setChatMessage] = useState("");
	const [senderEmail, setSenderEmail] = useState(null);
	const [receiverEmail, setReceiverEmail] = useState(null);
	const [chats, setChats] = useState([]);

	socket.on("already_joined", (msg) => {
		setConnection(false);
	});

	socket.on("joined", (msg) => {
		setConnection(true);

		const requestObj = {
			senderId: senderEmail,
			receiverId: receiverEmail,
		};

		axios
			.post("http://localhost:6001/chat/", requestObj)
			.then((response) => {
				console.log(response);
				if (response && response.data && response.data.length > 0) {
					console.log("Setting data, ", response.data);

					const transformedArray = response.data.map((item) => ({
						id: item._id,
						msg: item.message,
						sender: item.senderId,
						receiver: item.receiverId,
						createdDate: new Date(item.createdAt),
						updatedDate: new Date(item.updatedAt),
					}));

					setChats(transformedArray);
				}
				console.log(chats);
			})
			.catch((error) => console.error(error));

		console.log(chats);
	});

	socket.on("new_msg", (message) => {
		let transformedMap = message;
		transformedMap["createdDate"] = new Date(message["createdDate"]);
		transformedMap["updatedDate"] = new Date(message["updatedDate"]);

		setChats((prevChats) => [...prevChats, transformedMap]);
	});

	function handleInputChange(event) {
		if (event.target.name === "message") {
			setChatMessage(event.target.value);
		} else if (event.target.name === "semail") {
			setSenderEmail(event.target.value);
		} else if (event.target.name === "remail") {
			setReceiverEmail(event.target.value);
		}
	}

	function sendMessageToTheSocket() {
		socket.emit("new_msg", {
			message: chatMessage,
			receiverId: receiverEmail,
			senderId: senderEmail,
		});

		const message = {
			msg: chatMessage,
			receiver: receiverEmail,
			sender: senderEmail,
			id: generateSecureRandomKey(16),
			createdDate: new Date(),
			updatedDate: new Date(),
		};

		setChats((prevChats) => [...prevChats, message]);
		clearInputField();

		const messageData = {
			receiverId: receiverEmail,
			senderId: senderEmail,
			message: chatMessage,
		};

		axios
			.post("http://localhost:6001/chat/saveChats", messageData)
			.then((resp) => console.log(resp))
			.catch((err) => console.error(err));
	}

	function clearInputField() {
		setChatMessage("");
	}

	function connectToTheChatRoom() {
		if (senderEmail && receiverEmail) {
			socket.emit("join", {
				email: senderEmail,
				connectId: socket.id,
			});
		}
	}

	function generateSecureRandomKey(length = 8) {
		const array = new Uint8Array(length);
		window.crypto.getRandomValues(array);
		return Array.from(array, (byte) => byte.toString(16)).join("");
	}

	function handleFormSubmit(event) {
		event.preventDefault();

		if (event.which?.toLowerCase() === "enter") {
			this.sendMessageToTheSocket();
		}
	}

	function handleLoginSuccess(senderUsername) {
		setSenderEmail(senderUsername);
	}

	return (
		<Container
			className="d-flex justify-content-center align-items-center"
			style={{
				width: "80%", // Adjust the width as needed
				marginTop: "100px",
				marginLeft: "5%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			{senderEmail ? (
				<HomePage></HomePage>
			) : (
				<Register onLogin={handleLoginSuccess}></Register>
			)}
			{/*
			<Form onSubmit={handleFormSubmit}>
				<Form.Group controlId="chats" key="random">
					{successfullyConnected ? (
						<div>
							Hi {senderEmail}, you are currently chatting with{" "}
							{receiverEmail}!
							<br />
							<br />
							{chats && chats.length > 0
								? chats.map((item) => {
										return (
											<div key={item.id}>
												<ChatBubble
													message={item.msg}
													sender={item.sender}
													createdDate={
														item.createdDate
													}
													senderEmail={senderEmail}
												/>
											</div>
										);
								  })
								: ""}
							<br />
							<br />
							<Form.Label>Message {receiverEmail}</Form.Label>
							<br />
							<br />
							<Form.Control
								type="text"
								placeholder="Type your message here..."
								name="message"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "70%",
								}}
								value={chatMessage || ""}
							/>
							<Button
								variant="primary"
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									display: "inline-block",
									marginLeft: "10px",
									maxHeight: "40px",
								}}
								onClick={sendMessageToTheSocket}
							>
								<FaPaperPlane />
							</Button>
						</div>
					) : (
						<div>
							<p>
								Please provide your & receiver's email to
								connect
							</p>
							<br />
							<Form.Control
								type="email"
								placeholder="Enter your email"
								name="semail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>{" "}
							<br />
							<Form.Control
								type="email"
								placeholder="Enter receiver email"
								name="remail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>
							<Button
								variant="primary"
								onClick={connectToTheChatRoom}
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									width: "100%",
									display: "flex",
									alignItems: "center",
									justifyContent: "center",
								}}
							>
								<FaLink style={{ marginRight: "10px" }} />
								Connect
							</Button>
						</div>
					)}
				</Form.Group>
			</Form>
							*/}
		</Container>
	);
}

export default SocketCLientOne;
