import { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { Container } from "react-bootstrap";
import axios from "axios";
import Cookies from "js-cookie";
import Register from "./Register";
import HomePage from "./HomePage";

function SocketCLientOne() {
	const [chatMessage, setChatMessage] = useState("");
	const [senderEmail, setSenderEmail] = useState(null);
	const [receiverEmail, setReceiverEmail] = useState(null);
	const [chats, setChats] = useState([]);

	useEffect(() => {
		const myCookieValue = Cookies.get("anshul-chat-user");

		if (myCookieValue && myCookieValue !== senderEmail) {
			setSenderEmail(myCookieValue);
		} else {
			// Handle the case when the cookie value is not present
			// For example, set a default value or perform other actions
			setSenderEmail("");
		}
	}, [senderEmail]);

	const sendJoinRequest = () => {
		socket.emit("join", {
			email: senderEmail,
			connectId: socket.id,
		});
	};

	const socket = io("http://localhost:5001/", {
		withCredentials: true,
		extraHeaders: {
			ConnectWithAnshul: "Sure",
		},
	});

	socket.on("already_joined", (msg) => {});

	socket.on("joined", (msg) => {
		const requestObj = {
			senderId: senderEmail,
			receiverId: receiverEmail,
		};

		axios
			.post("http://localhost:6001/chat/", requestObj)
			.then((response) => {
				console.log(response);
				if (response && response.data && response.data.length > 0) {
					console.log("Setting data, ", response.data);

					const transformedArray = response.data.map((item) => ({
						id: item._id,
						msg: item.message,
						sender: item.senderId,
						receiver: item.receiverId,
						createdDate: new Date(item.createdAt),
						updatedDate: new Date(item.updatedAt),
					}));

					setChats(transformedArray);
				}
				console.log(chats);
			})
			.catch((error) => console.error(error));

		console.log(chats);
	});

	socket.on("new_msg", (message) => {
		let transformedMap = message;
		transformedMap["createdDate"] = new Date(message["createdDate"]);
		transformedMap["updatedDate"] = new Date(message["updatedDate"]);

		setChats((prevChats) => [...prevChats, transformedMap]);
	});

	function sendMessageToTheSocket() {
		socket.emit("new_msg", {
			message: chatMessage,
			receiverId: receiverEmail,
			senderId: senderEmail,
		});

		const message = {
			msg: chatMessage,
			receiver: receiverEmail,
			sender: senderEmail,
			id: generateSecureRandomKey(16),
			createdDate: new Date(),
			updatedDate: new Date(),
		};

		setChats((prevChats) => [...prevChats, message]);
		clearInputField();

		const messageData = {
			receiverId: receiverEmail,
			senderId: senderEmail,
			message: chatMessage,
		};

		axios
			.post("http://localhost:6001/chat/saveChats", messageData)
			.then((resp) => console.log(resp))
			.catch((err) => console.error(err));
	}

	function clearInputField() {
		setChatMessage("");
	}

	function generateSecureRandomKey(length = 8) {
		const array = new Uint8Array(length);
		window.crypto.getRandomValues(array);
		return Array.from(array, (byte) => byte.toString(16)).join("");
	}

	function handleLoginSuccess(senderUsername) {
		setSenderEmail(senderUsername);
		sendJoinRequest();
	}

	return (
		<Container
			className="d-flex justify-content-center align-items-center"
			style={{
				width: "80%", // Adjust the width as needed
				marginTop: "100px",
				marginLeft: "5%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			{senderEmail ? (
				<HomePage
					onSendMessage={sendMessageToTheSocket}
					onSetReceiverId={(data) => setReceiverEmail(data)}
					senderId={senderEmail}
					receiverId={receiverEmail}
					chats={chats}
				></HomePage>
			) : (
				<Register onLogin={handleLoginSuccess}></Register>
			)}
		</Container>
	);
}

export default SocketCLientOne;
