import { io } from "socket.io-client";
import { Container, Button, Form } from "react-bootstrap";
import { FaPaperPlane } from "react-icons/fa";

function SocketCLientOne() {
	const socket = io("http://localhost:5001/");

	socket.on("connect", () => {
		console.log(socket.id);

		const engine = socket.io.engine;
		console.log(engine.transport.name); // in most cases, prints "polling"

		engine.once("upgrade", () => {
			// called when the transport is upgraded (i.e. from HTTP long-polling to WebSocket)
			console.log(engine.transport.name); // in most cases, prints "websocket"
		});

		engine.on("packet", ({ type, data }) => {
			// called for each packet received
			console.log(
				`type of packet received: ${type}, and data is: ${JSON.stringify(
					data
				)}`
			);
		});

		engine.on("packetCreate", ({ type, data }) => {
			// called for each packet sent
			console.log(
				`type of packet created: ${type}, and data is: ${JSON.stringify(
					data
				)}`
			);
		});

		engine.on("drain", () => {
			// called when the write buffer is drained
		});

		engine.on("close", (reason) => {
			// called when the underlying connection is closed
		});
	});

	socket.on("data", (someData) => {
		console.log(someData);
	});

	function handleInputChange(event) {
		console.log(event.target.value);
	}

	return (
		<Container
			style={{
				position: "fixed",
				bottom: 0,
				right: 0,
				width: "30%",
				height: "60%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			<Form>
				<Form.Group controlId="exampleForm.ControlInput1">
					<Form.Label>Message</Form.Label>
					<br />
					<br />
					<Form.Control
						type="text"
						placeholder="Type your message here..."
						name="chat-input"
						onChange={handleInputChange}
						style={{
							borderRadius: "20px",
							border: "2px solid #ccc",
							padding: "10px",
							outline: "none",
							width: "70%",
						}}
					/>

					<Button
						variant="primary"
						style={{
							borderRadius: "20px",
							padding: "10px 20px",
							display: "inline-block",
						}}
					>
						<FaPaperPlane />
					</Button>
				</Form.Group>
			</Form>
		</Container>
	);
}

export default SocketCLientOne;
