import { useState } from "react";
import { io } from "socket.io-client";
import { Container, Button, Form } from "react-bootstrap";
import { FaPaperPlane, FaLink } from "react-icons/fa";
import axios from "axios";

function SocketCLientOne() {
	const socket = io("http://localhost:5001/");

	const [successfullyConnected, setConnection] = useState(false);
	const [chatMessage, setChatMessage] = useState("");
	const [senderEmail, setSenderEmail] = useState(null);
	const [receiverEmail, setReceiverEmail] = useState(null);
	const [chats, setChats] = useState([]);

	socket.on("already_joined", (msg) => {
		setConnection(false);
	});

	socket.on("joined", (msg) => {
		setConnection(true);

		axios
			.get("http://localhost:6001/chat/")
			.then((response) =>
				setChats((prevChats) => [...prevChats, response.data])
			)
			.catch((error) => console.error(error));

		console.log(chats);
	});

	socket.on("new_msg", (message) => {
		setChats((prevChats) => [...prevChats, message]);
	});

	function handleInputChange(event) {
		if (event.target.name === "message") {
			setChatMessage(event.target.value);
		} else if (event.target.name === "semail") {
			setSenderEmail(event.target.value);
		} else if (event.target.name === "remail") {
			setReceiverEmail(event.target.value);
		}
	}

	function sendMessageToTheSocket() {
		socket.emit("new_msg", {
			message: chatMessage,
			receiverId: receiverEmail,
			senderId: senderEmail,
		});

		const message = {
			msg: chatMessage,
			receiver: receiverEmail,
			sender: senderEmail,
			id: generateSecureRandomKey(16),
		};

		setChats((prevChats) => [...prevChats, message]);
		clearInputField();

		axios
			.post("http://localhost:6001/chat/saveChats", message)
			.then((resp) => console.log(resp))
			.catch((err) => console.error(err));
	}

	function clearInputField() {
		setChatMessage("");
	}

	function connectToTheChatRoom() {
		if (senderEmail && receiverEmail) {
			socket.emit("join", {
				email: senderEmail,
				connectId: socket.id,
			});
		}
	}

	const ChatBubble = ({ message, sender }) => {
		const isMyMessage = sender === senderEmail;
		return (
			<div
				style={{
					maxWidth: "70%",
					padding: "10px",
					borderRadius: "10px",
					margin: "10px",
					background: isMyMessage ? "#DCF8C6" : "#FFFFFF",
					alignSelf: isMyMessage ? "flex-end" : "flex-start",
					marginLeft: isMyMessage ? "auto" : "inherit", // Add some space on the left for my messages
					marginRight: isMyMessage ? "inherit" : "auto",
				}}
			>
				{message}
			</div>
		);
	};

	function generateSecureRandomKey(length = 8) {
		const array = new Uint8Array(length);
		window.crypto.getRandomValues(array);
		return Array.from(array, (byte) => byte.toString(16)).join("");
	}

	return (
		<Container
			className="d-flex justify-content-center align-items-center"
			style={{
				width: "80%", // Adjust the width as needed
				marginTop: "100px",
				marginLeft: "5%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			<Form>
				<Form.Group controlId="chats" key="random">
					{successfullyConnected ? (
						<div>
							Hi {senderEmail}, you are currently chatting with{" "}
							{receiverEmail}!
							<br />
							<br />
							{chats.map((item) => (
								<div key={item.id}>
									<ChatBubble
										message={item.msg}
										sender={item.sender}
									/>
								</div>
							))}
							<br />
							<br />
							<Form.Label>Message {receiverEmail}</Form.Label>
							<br />
							<br />
							<Form.Control
								type="text"
								placeholder="Type your message here..."
								name="message"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "70%",
								}}
								value={chatMessage || ""}
							/>
							<Button
								variant="primary"
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									display: "inline-block",
									marginLeft: "10px",
									maxHeight: "40px",
								}}
								onClick={sendMessageToTheSocket}
							>
								<FaPaperPlane />
							</Button>
						</div>
					) : (
						<div>
							<p>
								Please provide your & receiver's email to
								connect
							</p>
							<br />
							<Form.Control
								type="email"
								placeholder="Enter your email"
								name="semail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>{" "}
							<br />
							<Form.Control
								type="email"
								placeholder="Enter receiver email"
								name="remail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>
							<Button
								variant="primary"
								onClick={connectToTheChatRoom}
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									width: "100%",
									display: "flex",
									alignItems: "center",
									justifyContent: "center",
								}}
							>
								<FaLink style={{ marginRight: "10px" }} />
								Connect
							</Button>
						</div>
					)}
				</Form.Group>
			</Form>
		</Container>
	);
}

export default SocketCLientOne;
