import { io } from "socket.io-client";
import { Container, Button, Form } from "react-bootstrap";
import { FaPaperPlane } from "react-icons/fa";

function SocketCLientOne() {
	const socket = io("http://localhost:5001/");

	socket.on("connect", () => {
		console.log(socket.id);
		socket.emit("join", {
			email: "user1@email.com",
			connectId: socket.id,
		});
	});

	socket.on("data", (someData) => {
		console.log(someData);
	});

	let myMessage = null;
	function handleInputChange(event) {
		myMessage = event.target.value;
	}

	function sendMessageToTheSocket() {
		socket.emit("new_msg", myMessage);
	}

	return (
		<Container
			style={{
				position: "fixed",
				bottom: 0,
				right: 0,
				width: "30%",
				height: "60%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			<Form>
				<Form.Group controlId="exampleForm.ControlInput1">
					<Form.Label>Message</Form.Label>
					<br />
					<br />
					<Form.Control
						type="text"
						placeholder="Type your message here..."
						name="chat-input"
						onChange={handleInputChange}
						style={{
							borderRadius: "20px",
							border: "2px solid #ccc",
							padding: "10px",
							outline: "none",
							width: "70%",
						}}
					/>

					<Button
						variant="primary"
						style={{
							borderRadius: "20px",
							padding: "10px 20px",
							display: "inline-block",
							marginLeft: "10px",
							maxHeight: "40px",
						}}
						onClick={sendMessageToTheSocket}
					>
						<FaPaperPlane />
					</Button>
				</Form.Group>
			</Form>
		</Container>
	);
}

export default SocketCLientOne;
