import { useState } from "react";
import { io } from "socket.io-client";
import { Container, Button, Form } from "react-bootstrap";
import { FaPaperPlane, FaLink } from "react-icons/fa";

function SocketCLientOne() {
	const socket = io("http://localhost:5001/");

	const [successfullyConnected, setConnection] = useState(false);

	socket.on("already_joined", (msg) => {
		console.log(msg);
		setConnection(false);
	});

	socket.on("joined", (msg) => {
		console.log(`Connected...`, msg);
		setConnection(true);
	});

	let myMessage = null;
	let senderEmail = null;
	let receiverEmail = null;

	function handleInputChange(event) {
		if (event.target.name === "message") {
			myMessage = event.target.value;
		} else if (event.target.name === "semail") {
			senderEmail = event.target.value;
		} else if (event.target.name === "remail") {
			receiverEmail = event.target.value;
		}
	}

	function sendMessageToTheSocket() {
		socket.emit("new_msg", {
			message: myMessage,
			receiverId: receiverEmail,
			senderId: senderEmail,
		});
	}

	function connectToTheChatRoom() {
		console.log(senderEmail);
		if (senderEmail && receiverEmail) {
			console.log("connecting...");

			socket.emit("join", {
				email: senderEmail,
				connectId: socket.id,
			});
		}
	}

	return (
		<Container
			style={{
				position: "fixed",
				bottom: 0,
				right: 0,
				width: "30%",
				height: "60%",
				backgroundColor: "#f0f0f0", // Add your desired background color
				padding: "20px", // Add your desired padding
				boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add your desired box shadow
			}}
		>
			<Form>
				<Form.Group controlId="exampleForm.ControlInput1">
					{successfullyConnected ? (
						<div>
							<Form.Label>Message</Form.Label>
							<br />
							<br />
							<Form.Control
								type="text"
								placeholder="Type your message here..."
								name="message"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "70%",
								}}
							/>

							<Button
								variant="primary"
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									display: "inline-block",
									marginLeft: "10px",
									maxHeight: "40px",
								}}
								onClick={sendMessageToTheSocket}
							>
								<FaPaperPlane />
							</Button>
						</div>
					) : (
						<div>
							<p>
								Please provide your & receiver's email to
								connect
							</p>
							<br />
							<Form.Control
								type="email"
								placeholder="Enter your email"
								name="semail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>{" "}
							<br />
							<Form.Control
								type="email"
								placeholder="Enter receiver email"
								name="remail"
								onChange={handleInputChange}
								style={{
									borderRadius: "20px",
									border: "2px solid #ccc",
									padding: "10px",
									outline: "none",
									width: "100%",
									marginBottom: "10px",
								}}
							/>
							<Button
								variant="primary"
								onClick={connectToTheChatRoom}
								style={{
									borderRadius: "20px",
									padding: "10px 20px",
									width: "100%",
									display: "flex",
									alignItems: "center",
									justifyContent: "center",
								}}
							>
								<FaLink style={{ marginRight: "10px" }} />
								Connect
							</Button>
						</div>
					)}
				</Form.Group>
			</Form>
		</Container>
	);
}

export default SocketCLientOne;
